import React, { Component } from "react";
import axios from "axios";

class EventUpdate extends Component {

    constructor(props) {
        super(props);
        this.state = {
            id: this.props.match.params.id,
            title: 'NewEvent',
            startdate: '2021-05-15T10:00:00.008',
            enddate: '2021-06-15T10:00:00.008'
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        axios.get('http://localhost:8000/events/' + this.props.match.params.id)
            .then(result => {
                this.setState({ 
                                // eventobj: result.data,
                                title: result.data.title,
                                startdate: result.data.startdate,
                                enddate: result.data.enddate,
                                 });

                // console.log(this.state.eventobj);
            })
            .catch(error => { console.log(error) })
    }

    handleSubmit = () => {
        alert('Record will be updated for ID : ' + this.state.eventobj.id);
        const eventobj1 = {
            title: this.state.title,
            startdate: this.state.startdate,
            enddate: this.state.enddate           
        };
        console.log(eventobj1);
        axios.put('http://localhost:8000/events/' + this.props.match.params.id, this.state)
            .then(result => {
                console.log('Event Updated Successfully..!!' + result.data);
            })
            .catch(error => { console.log(error) })
    }

    handleChange = event => {
        const { name, value } = event.target;
        this.setState({ [name]: value })
        console.log(this.state);
    }

    render() {
        return (
            <div className="container">
                <div className="p-5 mb-4 bg-light rounded-3 text-center">
                    <div className="container-fluid py-5">
                        <h1 className="display-5 fw-bold">Update Event</h1>
                        <br />
                        <form onSubmit={this.handleSubmit.bind(this, this.props.match.params.id)}>
                            <div className="form-group">
                                <label>Event Title</label>
                                <input type="text"
                                    className="form-control"
                                    name="title"
                                    value={this.state.title}
                                    onChange={this.handleChange} />
                            </div>
                            <div className="form-group">
                                <label>Event Start Date</label>
                                <input type="text"
                                    className="form-control"
                                    name="startdate"
                                    value={this.state.startdate}
                                    onChange={this.handleChange} />
                            </div>
                            <div className="form-group">
                                <label>Event End Date</label>
                                <input type="text"
                                    className="form-control"
                                    name="enddate"
                                    value={this.state.enddate}
                                    onChange={this.handleChange} />
                            </div>
                            <input type="submit" className="btn btn-secondary" value="Update" />
                        </form>
                    </div>
                </div>
            </div>
        );
    }

}

export default EventUpdate;
