import React, { Component } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";

class EventDetail extends Component {

    constructor(props) {
        super(props);
        this.state = {
            event: {}
        }
    }

    componentDidMount() {
        axios.get('http://localhost:8000/events/' + this.props.match.params.id)
            .then(result => {
                this.setState({ event: result.data })
                console.log(this.state.event);
            })
            .catch(error => { console.log(error) })
    }

    deleteEvent(id) {
        axios.delete('http://localhost:8000/events/' + id)
            .then(result => {
                alert('Event Deleted with Id ' + id);
                this.getEvents();
                this.props.history.push('/event-list');
            })
            .catch(error => { console.log(error) })
    }

    render() {
        return (
            <div className="container">
                <div className="p-5 mb-4 bg-light rounded-3 text-center">
                    <div className="container-fluid py-5">
                        <h1 className="display-5 fw-bold">Event Detail</h1>

                        <div className="card">
                            <div className="card-header">
                                <b>Details of {this.state.event.name}</b>
                            </div>
                            <ul className="list-group list-group-flush">
                                <li className="list-group-item">Event Id: {this.state.event.id}</li>
                                <li className="list-group-item">Event Title: {this.state.event.title}</li>
                                <li className="list-group-item">Event Start Date: {this.state.event.startdate}</li>
                                <li className="list-group-item">Event End Date: {this.state.event.enddate}</li>
                            </ul>
                            <div className="card-footer">
                                <button className="btn btn-secondary"
                                    onClick={this.deleteEvent.bind(this, this.state.event.id)}>Delete</button> |
                                <NavLink to={'/event-update/' + this.state.event.id} className="btn btn-secondary">Update</NavLink> |
                                <NavLink to={'/event-list/'} className="btn btn-secondary">Back</NavLink>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default EventDetail;
