import './App.css';
import React, { Component } from "react";
import { BrowserRouter as Router, Link, NavLink, Route, Redirect } from "react-router-dom";
import Home from "./components/Home";
import EventList from "./components/EventList";
import Contact from "./components/Contact";
import EventDetail from "./components/EventDetail";

class App extends Component {

  state = {
    loggedIn: false
  }

  loginHandle = () => {
    this.setState(prevState => ({
      loggedIn: !prevState.loggedIn
    }))
  }

  render() {
    return (
      <Router>
        <div className="container">
          <header className="d-flex justify-content-center py-3 border-bottom">
            <ul className="nav nav-pills">
              <li className="nav-item"><NavLink to="/home" className="nav-link active">Home</NavLink></li> |
              <li className="nav-item"><NavLink to="/event-list" className="nav-link active">Events</NavLink></li> |
              <li className="nav-item"><NavLink to="/contact" className="nav-link active">Contact</NavLink></li>
            </ul>
          </header>
        </div >

        <Route path="/" exact render={Home} />
        <Route path="/home" exact render={Home} />
        <Route path="/event-list" exact render={EventList} />
        <Route path="/event-detail/:id" component={EventDetail}></Route>
        <Route path="/contact" render={Contact} />

      </Router>
    );
  }
}

export default App;
