import React, { Component } from "react";
import axios from "axios";
class EventAdd extends Component {

    constructor(props) {
        super(props);
        this.state = {
            title: 'NewEvent',
            startdate: '2021-05-15T10:00:00.008',
            enddate: '2021-06-15T10:00:00.008'
        };
    }

    handleSubmit = event => {
        event.preventDefault();
        const event1 = {
            title: this.state.title,
            startdate: this.state.startdate,
            enddate: this.state.enddate
        };
        console.log(event1);
        axios.post('http://localhost:8000/events', event1)
            .then(result => {
                console.log('Event Added Successfully..!!' + result.data);
            })
            .catch(error => { console.log(error) })
    }

    handleChange = event => {
        event.preventDefault();
        const { name, value } = event.target;
        this.setState({ [name]: value })
        console.log(this.state);

    }

    render() {
        return (
            <div className="container">
                <div className="p-5 mb-4 bg-light rounded-3 text-center">
                    <div className="container-fluid py-5">
                        <h1 className="display-5 fw-bold">Add New Event</h1>
                        <br />
                        <form onSubmit={this.handleSubmit}>
                            <div className="form-group">
                                <label>Event Title</label>
                                <input type="text"
                                    className="form-control"
                                    name="title"
                                    onChange={this.handleChange} />
                            </div>
                            <div className="form-group">
                                <label>Event Start Date</label>
                                <input type="text"
                                    className="form-control"
                                    name="startdate"
                                    onChange={this.handleChange} />
                            </div>
                            <div className="form-group">
                                <label>Event End Date</label>
                                <input type="text"
                                    className="form-control"
                                    name="enddate"
                                    onChange={this.handleChange} />
                            </div>
                            <input type="submit" className="btn btn-secondary" value="Add Event" />
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

export default EventAdd;
