import React, { Component } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";

class EventList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            // eventlist: []
        }
    }

    getEvents() {
        axios.get('http://localhost:8000/events')
            .then(result => {
                this.setState({ eventlist: result.data })
                console.log(this.state.eventlist);
            })
            .catch(error => {
                console.log("Error Caught : " + error)
            })
    }

    componentDidMount() {
        this.getEvents();
    }

    deleteEvent(id) {
        axios.delete('http://localhost:8000/events/' + id)
            .then(result => {
                alert('Event Deleted with Id ' + id);
                this.getEvents();
                this.props.history.push('/event-list');
            })
            .catch(error => { console.log(error) })
    }

    render() {
        return (

            <div className="container">
                <div className="p-5 mb-4 bg-light rounded-3 text-center">
                    <div className="container-fluid py-5">
                        {/* <NavLink to={'/event-add'} className="btn btn-secondary">Add New Event</NavLink> */}
                        <br /><br />
                        <h3 className="display-5 fw-bold">Event List</h3>
                        <br />
                        <table className="table table-hover">
                            <thead>
                                <tr>
                                    <th>Id</th>
                                    <th>Title</th>
                                    <th>Start Date</th>
                                    <th>End Date</th>
                                    {/* <th>Actions</th> */}
                                </tr>
                            </thead>
                            <tbody>
                                {this.state.events.map((eve, index) => {
                                    return (
                                        <tr key={index}>
                                            <td>{eve.id}</td>
                                            <td>{eve.title}</td>
                                            <td>{eve.startdate}</td>
                                            <td>{eve.enddate}</td>
                                            <td>
                                                <NavLink
                                                    to={'/event-detail/' + eve.id} className="btn btn-secondary">View Detail</NavLink> |
                                                <NavLink
                                                    to={'/event-update/' + eve.id} className="btn btn-secondary">Update</NavLink> |
                                                <button className="btn btn-secondary"
                                                    onClick={this.deleteEvent.bind(this, eve.id)}>Delete</button>
                                            </td>
                                        </tr>
                                    )
                                })}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        );
    }

}

export default EventList;
